def solution(A, X, y):
    sum_list = []
    for start in range(0, len(A) - y):
        inner_list = []
        x = X
        for val in range(start, len(A), y):
            inner_list.append(A[val])
            if x == 1:
                
                sum_list.append(sum(inner_list))
            x -= 1
            if x == 0:
                break
    return sum_list


